import React, {
  useState,
  useEffect
} from 'react';
import {
  useSelector,
  useDispatch
} from 'react-redux';

import {
  clearData
} from '../actions/searchActions';
import {
  fetchUsers
} from '../actions/userActions';
import {
  fetchRepos
} from '../actions/repoActions';

import logo from '../assets/img/git.svg';
import arrow from '../assets/img/down_caret.png';

const SearchForm = () => {

  const [listValue, setlistValue] = useState('users');
  const [text, setText] = useState('');
  const [plc, setPlc] = useState('Start typing to search users ..');
  const dataList = useSelector((state) => state.data.data);
  const dispatch = useDispatch();

  const changeText = (event) => {
    setText(event.target.value)
  }

  const changeListView = (event) => {
    setText('')
    setlistValue(event.target.value)
    setPlc(event.target.value === 'users' ? 'Start typing to search users ..' : 'Start typing to search repos ..')
    dispatch(clearData())
  }

  const updateData = () => {
    if (text.length > 2) {
      listValue === "users" ? dispatch(fetchUsers(text)) : dispatch(fetchRepos(text));
    } else if (dataList.length) {
      dispatch(clearData())
    }
  };

  useEffect(() => {
    updateData();
  }, [text])

  return ( <
    div className = "jumbotron jumbotron-fluid mt-5 text-center" >
    <
    div className = "container" >
    <
    div className = "heading" >
    <
    h3 className = "display-4 mb-3" >
    <
    img src = {
      logo
    }
    alt = "Logo"
    height = "25px"
    style = {
      {
        marginTop: '5px'
      }
    }
    />  <
    font style = {
      {
        marginLeft: '10px'
      }
    } > GitHub Searcher < /font> <
    /h3>  <
    p className = "detail-text" > Search users or repositories below < /p> <
    /div> <
    form id = "searchForm" >
    <
    input type = "text"
    className = "search-box"
    name = "text"
    placeholder = {
      plc
    }
    onChange = {
      changeText
    }
    value = {
      text
    }
    />  <
    select name = "listValue"
    className = "select-box"
    value = {
      listValue
    }
    onChange = {
      changeListView
    } >
    <
    option value = "users" > Users < /option>  <
    option value = "repos" > Repos < /option>  <
    /select>  <
    /form>  <
    /div>  <
    /div>
  );
}


export default SearchForm;
